// src/components/Hero/Hero.js
import React, { useState, useEffect } from 'react';
import { ArrowRight, Github, Linkedin, Mail, Sparkles, Code, Zap, Rocket, ExternalLink, Star, Heart, Coffee, Cpu, Lightbulb, Palette } from 'lucide-react';
import { personalInfo, socialLinks } from '../../data/portfolioData';
import TypewriterText from '../common/TypewriterText';

const Hero = ({ scrollToProjects, scrollToContact, openResume }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isVisible, setIsVisible] = useState(false);
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    setIsVisible(true);

    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const getIcon = (iconName) => {
    const icons = { Github, Linkedin, Mail };
    return icons[iconName];
  };

  const FloatingIcon = ({ icon: Icon, className, delay = 0, position }) => (
    <div 
      className={`absolute p-4 bg-gradient-to-br rounded-3xl shadow-2xl backdrop-blur-md border border-white/20
        hover:scale-125 transition-all duration-500 cursor-pointer group hover:shadow-[0_0_30px_rgba(139,69,255,0.6)]
        animate-float ${className} ${position}`}
      style={{ 
        animationDelay: `${delay}s`,
        transform: `translateY(${Math.sin(Date.now() * 0.001 + delay) * 10}px)`
      }}
    >
      <Icon className="w-6 h-6 text-white group-hover:rotate-12 transition-transform duration-300 drop-shadow-lg" />
      <div className="absolute inset-0 bg-gradient-to-r from-purple-400/30 to-pink-400/30 rounded-3xl opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
      <div className="absolute -inset-1 bg-gradient-to-r from-purple-500 to-pink-500 rounded-3xl blur opacity-0 group-hover:opacity-50 transition-opacity duration-300" />
    </div>
  );

  const ParticleField = () => (
    <div className="absolute inset-0 overflow-hidden pointer-events-none">
      {[...Array(50)].map((_, i) => (
        <div
          key={i}
          className="absolute animate-pulse"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 3}s`,
            animationDuration: `${2 + Math.random() * 4}s`
          }}
        >
          <div className="w-1 h-1 bg-gradient-to-r from-purple-400 to-pink-400 rounded-full opacity-60" />
        </div>
      ))}

      {/* Floating geometric shapes */}
      {[...Array(8)].map((_, i) => (
        <div
          key={`shape-${i}`}
          className="absolute animate-float opacity-20"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
            animationDuration: `${8 + Math.random() * 4}s`
          }}
        >
          {i % 3 === 0 && <div className="w-4 h-4 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full" />}
          {i % 3 === 1 && <div className="w-3 h-3 bg-gradient-to-r from-blue-500 to-cyan-500 rotate-45" />}
          {i % 3 === 2 && <div className="w-2 h-6 bg-gradient-to-r from-emerald-500 to-teal-500 rounded-full rotate-12" />}
        </div>
      ))}
    </div>
  );

  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative overflow-hidden">
      {/* Enhanced Multi-layer Background */}
      <div className="absolute inset-0 bg-gradient-to-br from-slate-950 via-purple-950/80 to-slate-900" />

      {/* Animated mesh gradient background */}
      <div className="absolute inset-0 opacity-40">
        <div className="absolute inset-0 bg-gradient-to-r from-purple-600/20 via-transparent to-pink-600/20 animate-pulse" />
        <div className="absolute inset-0 bg-gradient-to-b from-transparent via-blue-600/10 to-transparent animate-pulse delay-1000" />
      </div>

      {/* Dynamic background orbs */}
      <div className="absolute inset-0">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-purple-500/30 rounded-full blur-3xl animate-pulse-slow" />
        <div className="absolute bottom-1/4 right-1/4 w-[28rem] h-[28rem] bg-pink-500/25 rounded-full blur-3xl animate-pulse-slow delay-2000" />
        <div className="absolute top-1/2 left-1/2 w-80 h-80 bg-blue-500/20 rounded-full blur-3xl animate-pulse-slow delay-1000" />
        <div className="absolute top-3/4 left-1/3 w-64 h-64 bg-emerald-500/15 rounded-full blur-2xl animate-pulse-slow delay-3000" />
      </div>

      {/* Interactive cursor glow with trail effect */}
      <div 
        className="absolute w-96 h-96 bg-gradient-radial from-purple-400/30 via-pink-400/10 to-transparent rounded-full pointer-events-none blur-2xl transition-all duration-500 ease-out"
        style={{
          left: mousePosition.x - 192,
          top: mousePosition.y - 192,
          transform: `translate3d(0, ${scrollY * 0.1}px, 0)`,
        }}
      />

      <ParticleField />

      <div className="max-w-7xl mx-auto px-6 grid lg:grid-cols-2 gap-20 items-center relative z-10">
        {/* Left Content */}
        <div className={`space-y-8 transform transition-all duration-1000 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
          {/* Enhanced Status Badge */}
          <div className="inline-flex items-center space-x-4 bg-gradient-to-r from-purple-500/30 via-pink-500/30 to-purple-500/30 rounded-full px-8 py-4 border border-purple-400/50 backdrop-blur-md hover:border-purple-400/80 transition-all duration-500 hover:scale-105 group shadow-2xl hover:shadow-purple-500/50">
            <div className="relative">
              <Sparkles className="w-6 h-6 text-purple-300 group-hover:rotate-12 transition-transform duration-300" />
              <div className="absolute -top-1 -right-1 w-3 h-3 bg-gradient-to-r from-green-400 to-emerald-400 rounded-full animate-ping" />
              <div className="absolute -top-1 -right-1 w-3 h-3 bg-gradient-to-r from-green-400 to-emerald-400 rounded-full shadow-lg" />
            </div>
            <span className="text-base font-semibold text-white group-hover:text-purple-100 transition-colors">
              Available for exciting projects
            </span>
            <Heart className="w-5 h-5 text-pink-400 animate-pulse" />
            <div className="absolute inset-0 bg-gradient-to-r from-purple-600/20 to-pink-600/20 rounded-full opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
          </div>

          {/* Enhanced Main Heading */}
          <div className="space-y-6">
            <h1 className="text-5xl md:text-7xl lg:text-9xl font-black leading-tight tracking-tight">
              <div className="overflow-hidden">
                <span className={`block bg-gradient-to-r from-white via-purple-200 to-pink-200 bg-clip-text text-transparent transform transition-all duration-1000 delay-200 hover:scale-105 cursor-default ${isVisible ? 'translate-y-0' : 'translate-y-full'}`}>
                  Hi, I'm
                </span>
              </div>
              <div className="overflow-hidden">
                <span className={`block bg-gradient-to-r from-purple-400 via-pink-400 to-purple-600 bg-clip-text text-transparent transform transition-all duration-1000 delay-400 hover:scale-105 transition-transform cursor-default relative ${isVisible ? 'translate-y-0' : 'translate-y-full'}`}>
                  {personalInfo.name}
                  <div className="absolute -inset-4 bg-gradient-to-r from-purple-500/20 to-pink-500/20 blur-2xl opacity-0 hover:opacity-100 transition-opacity duration-500 rounded-3xl" />
                </span>
              </div>
            </h1>

            {/* Enhanced Typewriter Section */}
            <div className={`text-3xl lg:text-5xl transform transition-all duration-1000 delay-600 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-5 opacity-0'}`}>
              <TypewriterText
                strings={personalInfo.typewriterStrings}
                className="text-transparent bg-gradient-to-r from-purple-400 via-pink-400 to-purple-400 bg-clip-text font-bold drop-shadow-2xl"
              />
            </div>
          </div>

          {/* Enhanced Bio */}
          <p className={`text-xl lg:text-2xl text-gray-200 max-w-2xl leading-relaxed transform transition-all duration-1000 delay-800 font-light ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-5 opacity-0'}`}>
            {personalInfo.bio}
          </p>

          {/* Enhanced CTA Buttons */}
          <div className={`flex flex-col sm:flex-row gap-6 transform transition-all duration-1000 delay-1000 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-5 opacity-0'}`}>
            <button
              onClick={scrollToContact} 
              className="group relative px-10 py-5 bg-gradient-to-r from-purple-600 to-pink-600 rounded-3xl font-bold text-xl overflow-hidden transition-all duration-500 hover:scale-105 hover:shadow-2xl hover:shadow-purple-500/50 hover:-translate-y-2"
            >
              <span className="relative z-10 flex items-center justify-center space-x-3">
                <Rocket className="w-6 h-6 group-hover:rotate-12 transition-transform duration-300" />
                <span>Let's Create Magic</span>
                <ArrowRight className="w-6 h-6 group-hover:translate-x-2 transition-transform duration-300" />
              </span>
              <div className="absolute inset-0 bg-gradient-to-r from-pink-600 to-purple-600 opacity-0 group-hover:opacity-100 transition-all duration-500" />
              <div className="absolute -inset-1 bg-gradient-to-r from-purple-600 to-pink-600 rounded-3xl blur opacity-50 group-hover:opacity-80 transition-opacity duration-500" />
            </button>

            <button 
              onClick={scrollToProjects}
              className="px-10 py-5 border-2 border-purple-400/60 rounded-3xl font-bold text-xl hover:bg-purple-500/30 transition-all duration-500 hover:border-purple-400 hover:scale-105 hover:shadow-xl hover:shadow-purple-500/25 hover:-translate-y-2 backdrop-blur-md group"
            >
              <span className="bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent flex items-center space-x-3">
                <Palette className="w-6 h-6 group-hover:rotate-12 transition-transform duration-300 text-purple-400" />
                <span>Explore My Universe</span>
              </span>
            </button>

            <button 
              onClick={openResume}
              className="flex items-center justify-center space-x-3 px-10 py-5 bg-gradient-to-r from-emerald-500 to-teal-500 rounded-3xl font-bold text-xl hover:scale-105 transition-all duration-500 hover:shadow-2xl hover:shadow-emerald-500/50 hover:-translate-y-2 group"
            >
              <ExternalLink className="w-6 h-6 group-hover:rotate-12 transition-transform duration-300" />
              <span>View Resume</span>
            </button>
          </div>

          {/* Enhanced Social Links */}
          <div className={`flex space-x-6 transform transition-all duration-1000 delay-1200 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-5 opacity-0'}`}>
            {socialLinks.map((link, index) => {
              const Icon = getIcon(link.icon);
              return (
                <a
                  key={index}
                  href={link.url}
                  className="group relative p-5 bg-white/10 rounded-3xl hover:bg-white/20 transition-all duration-500 hover:scale-110 hover:shadow-2xl hover:-translate-y-2 backdrop-blur-md border border-white/20 hover:border-white/40"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <Icon className="w-7 h-7 text-gray-300 group-hover:text-white transition-colors duration-300 group-hover:rotate-12" />
                  <div className="absolute inset-0 bg-gradient-to-r from-purple-500/30 to-pink-500/30 rounded-3xl opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                  <div className="absolute -inset-1 bg-gradient-to-r from-purple-500 to-pink-500 rounded-3xl blur opacity-0 group-hover:opacity-50 transition-opacity duration-300" />
                </a>
              );
            })}
          </div>
        </div>

        {/* Enhanced Right Side Profile */}
        <div className={`relative transform transition-all duration-1000 delay-500 ${isVisible ? 'translate-x-0 opacity-100' : 'translate-x-10 opacity-0'}`}>
          {/* Main Profile Container */}
          <div className="relative w-[28rem] h-[28rem] mx-auto group">
            {/* Outer rotating rings */}
            <div className="absolute inset-0 bg-gradient-to-r from-purple-400 via-pink-400 to-purple-600 rounded-full animate-spin-slow opacity-90 shadow-2xl" />
            <div className="absolute inset-2 bg-gradient-to-r from-pink-500 via-purple-500 to-pink-500 rounded-full animate-reverse-spin opacity-70" />
            <div className="absolute inset-4 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 rounded-full animate-spin-slow opacity-50" />

            {/* Inner container */}
            <div className="absolute inset-8 bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 rounded-full flex items-center justify-center overflow-hidden shadow-2xl border-4 border-white/20 backdrop-blur-sm">
              {/* Profile Image */}
              <div className="absolute inset-6 rounded-full overflow-hidden group-hover:scale-110 transition-transform duration-700 shadow-2xl">
                <div className="absolute inset-0 bg-gradient-to-br from-purple-400/60 via-transparent to-pink-400/60 z-10" />
                <img 
                  src="/images/profile/avatar.jpg" 
                  alt="Darshan Dubey" 
                  className="w-full h-full object-cover transition-all duration-700 group-hover:scale-125 group-hover:rotate-3 group-hover:brightness-110"
                />
              </div>
            </div>

            {/* Enhanced Floating Icons */}
            <FloatingIcon 
              icon={Code} 
              className="from-blue-500 to-cyan-500" 
              position="-top-8 -right-8"
              delay={0}
            />
            <FloatingIcon 
              icon={Zap} 
              className="from-yellow-500 to-orange-500" 
              position="-bottom-8 -left-8"
              delay={0.5}
            />
            <FloatingIcon 
              icon={Rocket} 
              className="from-red-500 to-pink-500" 
              position="top-1/2 -right-16"
              delay={1}
            />
            <FloatingIcon 
              icon={Star} 
              className="from-purple-500 to-indigo-500" 
              position="top-8 -left-12"
              delay={1.5}
            />
            <FloatingIcon 
              icon={Coffee} 
              className="from-amber-500 to-orange-500" 
              position="bottom-8 -right-12"
              delay={2}
            />
            <FloatingIcon 
              icon={Cpu} 
              className="from-green-500 to-emerald-500" 
              position="bottom-1/3 -left-12"
              delay={2.5}
            />
            <FloatingIcon 
              icon={Lightbulb} 
              className="from-pink-500 to-rose-500" 
              position="top-1/3 -right-12"
              delay={3}
            />

            {/* Enhanced Orbiting elements */}
            <div className="absolute inset-0 animate-spin-slow">
              <div className="absolute -top-6 left-1/2 w-4 h-4 bg-gradient-to-r from-purple-400 to-pink-400 rounded-full shadow-lg shadow-purple-400/50" />
              <div className="absolute -bottom-6 left-1/2 w-3 h-3 bg-gradient-to-r from-pink-400 to-purple-400 rounded-full shadow-lg shadow-pink-400/50" />
              <div className="absolute top-1/2 -left-6 w-3 h-3 bg-gradient-to-r from-blue-400 to-cyan-400 rounded-full shadow-lg shadow-blue-400/50" />
              <div className="absolute top-1/2 -right-6 w-4 h-4 bg-gradient-to-r from-emerald-400 to-teal-400 rounded-full shadow-lg shadow-emerald-400/50" />
            </div>

            {/* Additional orbital ring */}
            <div className="absolute inset-0 animate-reverse-spin">
              <div className="absolute top-16 left-16 w-2 h-2 bg-yellow-400 rounded-full shadow-lg shadow-yellow-400/50" />
              <div className="absolute bottom-16 right-16 w-2 h-2 bg-red-400 rounded-full shadow-lg shadow-red-400/50" />
              <div className="absolute top-16 right-16 w-2 h-2 bg-green-400 rounded-full shadow-lg shadow-green-400/50" />
              <div className="absolute bottom-16 left-16 w-2 h-2 bg-blue-400 rounded-full shadow-lg shadow-blue-400/50" />
            </div>
          </div>

          {/* Floating skill badges */}
          <div className="absolute -top-16 left-16 bg-gradient-to-r from-purple-500 to-pink-500 text-white px-4 py-2 rounded-full text-sm font-bold animate-bounce opacity-80">
            Frontend
          </div>
