import React, { useState, useEffect, useRef } from 'react';
import { 
  Menu, X, Github, Linkedin, Mail, ExternalLink, 
  Code, Palette, Zap, Target, Rocket, Monitor,
  Database, GitBranch, Star, ArrowRight, Play,
  MousePointer, Sparkles, Heart
} from 'lucide-react';

const ModernPortfolio = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [activeSection, setActiveSection] = useState('home');
  const heroRef = useRef(null);

  // Mouse tracking for interactive effects
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Intersection Observer for active section
  useEffect(() => {
    const sections = document.querySelectorAll('section[id]');
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setActiveSection(entry.target.id);
          }
        });
      },
      { threshold: 0.6 }
    );

    sections.forEach((section) => observer.observe(section));
    return () => sections.forEach((section) => observer.unobserve(section));
  }, []);

  const TypewriterText = ({ strings, className }) => {
    const [currentString, setCurrentString] = useState('');
    const [stringIndex, setStringIndex] = useState(0);
    const [charIndex, setCharIndex] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);

    useEffect(() => {
      const timeout = setTimeout(() => {
        const current = strings[stringIndex];

        if (!isDeleting && charIndex < current.length) {
          setCurrentString(current.substring(0, charIndex + 1));
          setCharIndex(prev => prev + 1);
        } else if (isDeleting && charIndex > 0) {
          setCurrentString(current.substring(0, charIndex - 1));
          setCharIndex(prev => prev - 1);
        } else if (!isDeleting && charIndex === current.length) {
          setTimeout(() => setIsDeleting(true), 1500);
        } else if (isDeleting && charIndex === 0) {
          setIsDeleting(false);
          setStringIndex((prev) => (prev + 1) % strings.length);
        }
      }, isDeleting ? 50 : 100);

      return () => clearTimeout(timeout);
    }, [charIndex, isDeleting, stringIndex, strings]);

    return <span className={className}>{currentString}<span className="animate-pulse">|</span></span>;
  };

