import React, { useState, useEffect } from 'react';
import { ExternalLink, Star, Play, Github, Eye, Zap, Sparkles, Rocket, Code, Globe } from 'lucide-react';
import { projects } from '../../data/portfolioData';

const Projects = () => {
  const [hoveredProject, setHoveredProject] = useState(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [isVisible, setIsVisible] = useState(false);

  // Extract unique categories from projects data
  const getProjectCategories = () => {
    const categories = projects.reduce((acc, project) => {
      if (project.tech.some(tech => ['React', 'Next.js', 'Angular', 'Vue.js'].includes(tech))) {
        acc.add('Frontend');
      } else if (project.tech.some(tech => ['Node.js', 'Python', 'APIs'].includes(tech))) {
        acc.add('Full Stack');
      } else if (project.tech.some(tech => ['HTML', 'CSS', 'JavaScript'].includes(tech))) {
        acc.add('Web Apps');
      }
      return acc;
    }, new Set());
    return ['All', ...Array.from(categories)];
  };

  const categories = getProjectCategories();

  useEffect(() => {
    setIsVisible(true);

    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const getProjectCategory = (project) => {
    if (project.tech.some(tech => ['React', 'Next.js', 'Angular', 'Vue.js'].includes(tech))) {
      return 'Frontend';
    } else if (project.tech.some(tech => ['Node.js', 'Python', 'APIs'].includes(tech))) {
      return 'Full Stack';
    } else {
      return 'Web Apps';
    }
  };

  const filteredProjects = selectedCategory === 'All' 
    ? projects 
    : projects.filter(project => getProjectCategory(project) === selectedCategory);

  return (
    <section 
      id="projects" 
      className="min-h-screen py-20 relative overflow-hidden"
      style={{
        background: 'linear-gradient(135deg, #0f0f23 0%, #1a0033 25%, #000 50%, #330066 75%, #0f0f23 100%)',
        backgroundSize: '400% 400%',
        animation: 'gradientShift 15s ease infinite'
      }}
    >
      {/* Dynamic Background Effects */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Floating Particles */}
        {[...Array(50)].map((_, i) => (
          <div
            key={i}
            className="absolute w-1 h-1 bg-white rounded-full opacity-20"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animation: `float ${5 + Math.random() * 10}s linear infinite`,
              animationDelay: `${Math.random() * 5}s`
            }}
          />
        ))}

        {/* Glowing Orbs */}
        <div 
          className="absolute w-96 h-96 rounded-full opacity-30 blur-3xl"
          style={{
            background: 'radial-gradient(circle, #ff00ff 0%, transparent 70%)',
            left: '10%',
            top: '20%',
            animation: 'pulse 4s ease-in-out infinite'
          }}
        />
        <div 
          className="absolute w-80 h-80 rounded-full opacity-20 blur-3xl"
          style={{
            background: 'radial-gradient(circle, #00ffff 0%, transparent 70%)',
            right: '10%',
            bottom: '20%',
            animation: 'pulse 6s ease-in-out infinite reverse'
          }}
        />
        <div 
          className="absolute w-64 h-64 rounded-full opacity-25 blur-2xl"
          style={{
            background: 'radial-gradient(circle, #ffff00 0%, transparent 70%)',
            left: '50%',
            top: '50%',
            transform: 'translate(-50%, -50%)',
            animation: 'rotate 20s linear infinite'
          }}
        />
      </div>

      {/* Magnetic Cursor Effect */}
      <div 
        className="fixed w-6 h-6 pointer-events-none z-50 transition-all duration-200 ease-out rounded-full border-2 border-cyan-400"
        style={{
          left: mousePosition.x - 12,
          top: mousePosition.y - 12,
          background: hoveredProject ? 'radial-gradient(circle, rgba(0,255,255,0.3) 0%, transparent 70%)' : 'transparent',
          boxShadow: hoveredProject ? '0 0 30px rgba(0,255,255,0.5)' : 'none'
        }}
      />

      <div className="max-w-7xl mx-auto px-6 relative z-10">
        {/* Epic Header */}
        <div 
          className={`text-center mb-20 transform transition-all duration-1000 ${
            isVisible ? 'translate-y-0 opacity-100' : 'translate-y-20 opacity-0'
          }`}
        >
          <div className="relative inline-block mb-8">
            <h2 
              className="text-8xl md:text-9xl font-black mb-6 relative"
              style={{
                background: 'linear-gradient(45deg, #ff00ff, #00ffff, #ffff00, #ff00ff)',
                backgroundSize: '300% 300%',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                animation: 'gradientMove 3s ease infinite'
              }}
            >
              PROJECTS
            </h2>
            <div 
              className="absolute inset-0 text-8xl md:text-9xl font-black opacity-20 blur-sm"
              style={{
                background: 'linear-gradient(45deg, #ff00ff, #00ffff, #ffff00)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                transform: 'scale(1.05)'
              }}
            >
              PROJECTS
            </div>
          </div>

          <div className="flex items-center justify-center space-x-3 mb-8">
            <div className="h-px w-20 bg-gradient-to-r from-transparent to-cyan-400"></div>
            <div className="flex items-center space-x-2 px-6 py-3 rounded-full border border-cyan-400/30 backdrop-blur-sm">
              <Sparkles className="w-5 h-5 text-cyan-400" style={{ animation: 'sparkle 2s ease-in-out infinite' }} />
              <span className="text-cyan-300 font-semibold text-lg">Innovative Digital Experiences</span>
              <Code className="w-5 h-5 text-cyan-400" style={{ animation: 'sparkle 2s ease-in-out infinite 0.5s' }} />
            </div>
            <div className="h-px w-20 bg-gradient-to-l from-transparent to-cyan-400"></div>
          </div>

          <p className="text-xl text-gray-300 max-w-4xl mx-auto leading-relaxed">
            ðŸš€ Where code meets creativity and imagination becomes reality. Each project is crafted with passion, 
            precision, and a touch of magic âœ¨
          </p>
        </div>

        {/* Interactive Category Filter */}
        <div 
          className={`flex justify-center mb-16 transform transition-all duration-1000 delay-300 ${
            isVisible ? 'translate-y-0 opacity-100' : 'translate-y-20 opacity-0'
          }`}
        >
          <div 
            className="flex space-x-2 p-3 rounded-3xl border border-cyan-400/20 backdrop-blur-xl"
            style={{
              background: 'linear-gradient(135deg, rgba(0,255,255,0.1) 0%, rgba(255,0,255,0.1) 50%, rgba(255,255,0,0.1) 100%)'
            }}
          >
            {categories.map((category, index) => (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                className={`px-8 py-4 rounded-2xl font-bold transition-all duration-500 transform hover:scale-105 relative overflow-hidden ${
                  selectedCategory === category
                    ? 'text-black shadow-2xl'
                    : 'text-cyan-300 hover:text-white'
                }`}
                style={{
                  background: selectedCategory === category 
                    ? 'linear-gradient(45deg, #00ffff, #ff00ff, #ffff00)' 
                    : 'transparent',
                  backgroundSize: '200% 200%',
                  animation: selectedCategory === category ? 'gradientMove 2s ease infinite' : 'none',
                  animationDelay: `${index * 100}ms`
                }}
              >
                <span className="relative z-10">{category}</span>
                {selectedCategory !== category && (
                  <div 
                    className="absolute inset-0 bg-gradient-to-r from-cyan-500/20 to-purple-500/20 opacity-0 hover:opacity-100 transition-opacity duration-300"
                  />
                )}
              </button>
            ))}
          </div>
        </div>

        {/* Revolutionary Projects Grid */}
        <div className="grid lg:grid-cols-2 xl:grid-cols-3 gap-8">
          {filteredProjects.map((project, index) => (
            <div
              key={project.id}
              className={`group relative rounded-3xl overflow-hidden cursor-pointer transform transition-all duration-700 hover:scale-105 ${
                project.featured ? 'lg:col-span-2 xl:col-span-3 h-[600px]' : 'h-[500px]'
              }`}
              onMouseEnter={() => setHoveredProject(project.id)}
              onMouseLeave={() => setHoveredProject(null)}
              style={{
                transform: `translateY(${isVisible ? 0 : 50}px)`,
                opacity: isVisible ? 1 : 0,
                transition: `all 0.8s ease ${index * 150}ms`,
                background: 'linear-gradient(135deg, rgba(0,0,0,0.8) 0%, rgba(20,20,40,0.9) 100%)',
                border: '1px solid rgba(0,255,255,0.2)',
                boxShadow: hoveredProject === project.id 
                  ? '0 20px 60px rgba(0,255,255,0.3), 0 0 0 2px rgba(0,255,255,0.5)' 
                  : '0 10px 30px rgba(0,0,0,0.5)'
              }}
            >
              {/* Dynamic Background */}
              <div className="absolute inset-0">
                <img 
                  src={project.image} 
                  alt={project.title}
                  className="w-full h-full object-cover transition-all duration-700 group-hover:scale-110"
                  style={{
                    filter: 'brightness(0.7) contrast(1.2) saturate(1.3)',
                  }}
                />
                <div 
                  className="absolute inset-0"
                  style={{
                    background: 'linear-gradient(135deg, rgba(0,0,0,0.7) 0%, rgba(0,255,255,0.1) 50%, rgba(255,0,255,0.1) 100%)'
                  }}
                />
              </div>

              {/* Holographic Border Effect */}
              <div 
                className="absolute inset-0 rounded-3xl opacity-0 group-hover:opacity-100 transition-opacity duration-500"
                style={{
                  background: 'linear-gradient(45deg, transparent, rgba(0,255,255,0.3), transparent, rgba(255,0,255,0.3), transparent)',
                  backgroundSize: '400% 400%',
                  animation: 'gradientMove 3s ease infinite',
                  mask: 'linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0)',
                  maskComposite: 'xor',
                  padding: '2px'
                }}
              />

              {/* Content */}
              <div className="relative h-full p-8 flex flex-col justify-between z-10">
                {/* Top Section */}
                <div className="flex items-start justify-between">
                  <div className="flex items-center space-x-4">
                    <div 
                      className="text-6xl font-black"
                      style={{
                        background: 'linear-gradient(45deg, #00ffff, #ff00ff)',
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent',
                        filter: 'drop-shadow(0 0 10px rgba(0,255,255,0.5))'
                      }}
                    >
                      {project.id.toString().padStart(2, '0')}
                    </div>

                    {project.featured && (
                      <div 
                        className="flex items-center space-x-2 px-4 py-2 rounded-full text-sm font-bold text-black"
                        style={{
                          background: 'linear-gradient(45deg, #ffff00, #ff8800)',
                          boxShadow: '0 0 20px rgba(255,255,0,0.5)',
                          animation: 'pulse 2s ease-in-out infinite'
                        }}
                      >
                        <Star className="w-4 h-4" />
                        <span>FEATURED</span>
                      </div>
                    )}
                  </div>

                  <div 
                    className="flex items-center space-x-1 px-3 py-1 rounded-full text-xs font-bold text-black"
                    style={{
                      background: 'linear-gradient(45deg, #00ff00, #00ffff)',
                      boxShadow: '0 0 15px rgba(0,255,255,0.5)'
                    }}
                  >
                    <div className="w-2 h-2 bg-current rounded-full" style={{ animation: 'pulse 1s ease-in-out infinite' }} />
                    <span>LIVE</span>
                  </div>
                </div>

                {/* Bottom Section */}
                <div className="space-y-6">
                  <div>
                    <span 
                      className="text-sm font-bold px-4 py-2 rounded-full border"
                      style={{
                        color: '#00ffff',
                        borderColor: '#00ffff',
                        background: 'rgba(0,255,255,0.1)',
                        boxShadow: '0 0 10px rgba(0,255,255,0.3)'
                      }}
                    >
                      {getProjectCategory(project)}
                    </span>
                  </div>

                  <h3 
                    className="text-4xl font-black mb-4 transition-all duration-500"
                    style={{
                      color: hoveredProject === project.id ? 'transparent' : '#fff',
                      background: hoveredProject === project.id ? 'linear-gradient(45deg, #00ffff, #ff00ff)' : 'none',
                      WebkitBackgroundClip: hoveredProject === project.id ? 'text' : 'unset',
                      textShadow: hoveredProject === project.id ? 'none' : '0 0 20px rgba(255,255,255,0.5)'
                    }}
                  >
                    {project.title}
                  </h3>

                  <div 
                    className="rounded-2xl p-4 border backdrop-blur-sm transition-all duration-300"
                    style={{
                      background: 'rgba(0,0,0,0.6)',
                      borderColor: hoveredProject === project.id ? '#00ffff' : 'rgba(255,255,255,0.1)',
                      boxShadow: hoveredProject === project.id ? '0 0 20px rgba(0,255,255,0.3)' : 'none'
                    }}
                  >
                    <p className="text-gray-200 text-lg leading-relaxed">
                      {project.description}
                    </p>
                  </div>

                  {/* Tech Stack */}
                  <div className="flex flex-wrap gap-3">
                    {project.tech.map((tech, techIndex) => (
                      <span
                        key={tech}
                        className="px-4 py-2 rounded-full text-sm font-semibold transition-all duration-300 hover:scale-110 cursor-pointer"
                        style={{
                          background: 'rgba(255,255,255,0.1)',
                          border: '1px solid rgba(0,255,255,0.3)',
                          color: '#fff',
                          boxShadow: '0 0 10px rgba(0,255,255,0.2)',
                          animation: `fadeInUp 0.6s ease ${techIndex * 100}ms both`
                        }}
                        onMouseEnter={(e) => {
                          e.target.style.background = 'linear-gradient(45deg, rgba(0,255,255,0.3), rgba(255,0,255,0.3))';
                          e.target.style.borderColor = '#00ffff';
                          e.target.style.boxShadow = '0 0 20px rgba(0,255,255,0.5)';
                        }}
                        onMouseLeave={(e) => {
                          e.target.style.background = 'rgba(255,255,255,0.1)';
                          e.target.style.borderColor = 'rgba(0,255,255,0.3)';
                          e.target.style.boxShadow = '0 0 10px rgba(0,255,255,0.2)';
                        }}
                      >
                        {tech}
                      </span>
                    ))}
                  </div>

                  {/* Epic Action Buttons */}
                  <div className="flex space-x-4 pt-4">
                    <a
                      href={project.liveUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="group/btn relative flex items-center space-x-3 px-8 py-4 rounded-2xl font-bold transition-all duration-500 hover:scale-105 text-black overflow-hidden"
                      style={{
                        background: 'linear-gradient(45deg, #00ffff, #ff00ff)',
                        boxShadow: '0 10px 30px rgba(0,255,255,0.3)',
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.boxShadow = '0 20px 60px rgba(0,255,255,0.5)';
                        e.target.style.transform = 'translateY(-5px) scale(1.05)';
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.boxShadow = '0 10px 30px rgba(0,255,255,0.3)';
                        e.target.style.transform = 'translateY(0) scale(1)';
                      }}
                    >
                      <Globe className="w-5 h-5 relative z-10" />
                      <span className="relative z-10 font-black">LAUNCH</span>
                      <div 
                        className="absolute inset-0 opacity-0 group-hover/btn:opacity-100 transition-opacity duration-300"
                        style={{
                          background: 'linear-gradient(45deg, #ffff00, #ff8800)',
                        }}
                      />
                    </a>

                    <a
                      href={project.githubUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="group/btn relative flex items-center space-x-3 px-8 py-4 rounded-2xl font-bold transition-all duration-500 hover:scale-105 text-cyan-300 overflow-hidden border-2 border-cyan-400/50 backdrop-blur-sm"
                      style={{
                        background: 'rgba(0,255,255,0.1)',
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = 'rgba(0,255,255,0.2)';
                        e.target.style.borderColor = '#00ffff';
                        e.target.style.boxShadow = '0 0 30px rgba(0,255,255,0.5)';
                        e.target.style.color = '#fff';
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = 'rgba(0,255,255,0.1)';
                        e.target.style.borderColor = 'rgba(0,255,255,0.5)';
                        e.target.style.boxShadow = 'none';
                        e.target.style.color = 'rgb(103 232 249)';
                      }}
                    >
                      <Github className="w-5 h-5 relative z-10" />
                      <span className="relative z-10 font-black">CODE</span>
                    </a>
                  </div>
                </div>
              </div>

              {/* Hover Glow Effect */}
              <div 
                className="absolute top-4 right-4 opacity-0 group-hover:opacity-100 transition-all duration-500 transform group-hover:scale-110"
              >
                <ExternalLink 
                  className="w-8 h-8 text-cyan-400" 
                  style={{
                    filter: 'drop-shadow(0 0 10px rgba(0,255,255,0.8))',
                    animation: hoveredProject === project.id ? 'sparkle 1s ease-in-out infinite' : 'none'
                  }}
                />
              </div>
            </div>
          ))}
        </div>

        {/* Mind-Blowing Call to Action */}
        <div 
          className={`text-center mt-24 transform transition-all duration-1000 delay-500 ${
            isVisible ? 'translate-y-0 opacity-100' : 'translate-y-20 opacity-0'
          }`}
        >
          <div 
            className="inline-flex items-center space-x-4 px-12 py-6 rounded-3xl border-2 backdrop-blur-xl cursor-pointer group transition-all duration-500 hover:scale-105"
            style={{
              background: 'linear-gradient(135deg, rgba(0,255,255,0.1) 0%, rgba(255,0,255,0.1) 50%, rgba(255,255,0,0.1) 100%)',
              borderColor: '#00ffff',
              boxShadow: '0 0 30px rgba(0,255,255,0.3)'
            }}
            onMouseEnter={(e) => {
              e.target.style.background = 'linear-gradient(135deg, rgba(0,255,255,0.2) 0%, rgba(255,0,255,0.2) 50%, rgba(255,255,0,0.2) 100%)';
              e.target.style.boxShadow = '0 0 50px rgba(0,255,255,0.5)';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = 'linear-gradient(135deg, rgba(0,255,255,0.1) 0%, rgba(255,0,255,0.1) 50%, rgba(255,255,0,0.1) 100%)';
              e.target.style.boxShadow = '0 0 30px rgba(0,255,255,0.3)';
            }}
          >
            <Eye className="w-8 h-8 text-cyan-400 group-hover:animate-pulse" />
            <span 
              className="text-2xl font-black"
              style={{
                background: 'linear-gradient(45deg, #00ffff, #ff00ff)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent'
              }}
            >
              Explore The Universe of Code
            </span>
            <Rocket className="w-8 h-8 text-purple-400 group-hover:animate-bounce" />
          </div>
        </div>
      </div>

      {/* Inline Styles for Animations */}
      <style dangerouslySetInnerHTML={{
        __html: `
          @keyframes gradientShift {
            0%, 100% { background-position: 0% 50%; }
            50% { background-position: 100% 50%; }
          }

          @keyframes gradientMove {
            0%, 100% { background-position: 0% 50%; }
            50% { background-position: 100% 50%; }
          }

          @keyframes float {
            0%, 100% { transform: translateY(0px) rotate(0deg); }
            33% { transform: translateY(-20px) rotate(120deg); }
            66% { transform: translateY(10px) rotate(240deg); }
          }

          @keyframes pulse {
            0%, 100% { transform: scale(1); opacity: 0.7; }
            50% { transform: scale(1.1); opacity: 1; }
          }

          @keyframes rotate {
            from { transform: translate(-50%, -50%) rotate(0deg); }
            to { transform: translate(-50%, -50%) rotate(360deg); }
          }

          @keyframes sparkle {
            0%, 100% { opacity: 1; transform: scale(1); }
            50% { opacity: 0.5; transform: scale(1.2); }
          }

          @keyframes fadeInUp {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
          }
        `
      }} />
    </section>
  );
};

export default Projects;
