// src/components/Hero/Hero.js
import React, { useState, useEffect } from 'react';
import { ArrowRight, Github, Linkedin, Mail, Sparkles, Code, Zap, Rocket, ExternalLink, Star, Heart, Coffee, Music } from 'lucide-react';
import { personalInfo, socialLinks } from '../../data/portfolioData';
import TypewriterText from '../common/TypewriterText';

const Hero = ({ scrollToProjects, scrollToContact, openResume }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    // Generate floating particles
    const generateParticles = () => {
      const newParticles = [];
      for (let i = 0; i < 50; i++) {
        newParticles.push({
          id: i,
          x: Math.random() * window.innerWidth,
          y: Math.random() * window.innerHeight,
          size: Math.random() * 4 + 1,
          speed: Math.random() * 2 + 0.5,
          opacity: Math.random() * 0.5 + 0.2,
        });
      }
      setParticles(newParticles);
    };

    window.addEventListener('mousemove', handleMouseMove);
    generateParticles();

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  const getIcon = (iconName) => {
    const icons = { Github, Linkedin, Mail };
    return icons[iconName];
  };

  const floatingIcons = [Star, Heart, Coffee, Music, Code, Zap, Rocket, Sparkles];

  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative overflow-hidden">
      {/* Animated Background Layers */}
      <div className="absolute inset-0 bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900" />

      {/* Dynamic Mesh Gradient */}
      <div 
        className="absolute inset-0 opacity-40"
        style={{
          background: `radial-gradient(circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(168,85,247,0.4) 0%, rgba(236,72,153,0.3) 25%, rgba(59,130,246,0.2) 50%, transparent 70%)`
        }}
      />

      {/* Floating Particles */}
      <div className="absolute inset-0 overflow-hidden">
        {particles.map((particle) => (
          <div
            key={particle.id}
            className="absolute bg-white rounded-full animate-float"
            style={{
              left: `${particle.x}px`,
              top: `${particle.y}px`,
              width: `${particle.size}px`,
              height: `${particle.size}px`,
              opacity: particle.opacity,
              animationDelay: `${particle.id * 0.1}s`,
              animationDuration: `${particle.speed + 2}s`,
            }}
          />
        ))}
      </div>

      {/* Geometric Shapes */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-20 left-20 w-32 h-32 bg-gradient-to-r from-purple-500/20 to-pink-500/20 rounded-full blur-xl animate-pulse" />
        <div className="absolute top-40 right-32 w-24 h-24 bg-gradient-to-r from-blue-500/20 to-cyan-500/20 rounded-xl rotate-45 animate-spin-slow" />
        <div className="absolute bottom-32 left-40 w-20 h-20 bg-gradient-to-r from-green-500/20 to-emerald-500/20 rounded-full animate-bounce" />
        <div className="absolute bottom-20 right-20 w-28 h-28 bg-gradient-to-r from-orange-500/20 to-red-500/20 rounded-lg rotate-12 animate-pulse" />
      </div>

      {/* Glowing Grid */}
      <div 
        className="absolute inset-0 opacity-30"
        style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23a855f7' fill-opacity='0.05'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`
        }}
      />

      <div className="max-w-7xl mx-auto px-6 grid lg:grid-cols-2 gap-12 items-center relative z-10">
        <div className="space-y-8">
          <div className="space-y-6">
            {/* Status Badge with Glow */}
            <div className="inline-flex items-center space-x-3 bg-gradient-to-r from-purple-500/30 to-pink-500/30 backdrop-blur-sm rounded-full px-6 py-3 border border-purple-500/50 shadow-lg hover:shadow-purple-500/25 transition-all duration-300">
              <div className="relative">
                <Sparkles className="w-5 h-5 text-purple-400 animate-pulse" />
                <div className="absolute inset-0 w-5 h-5 bg-purple-400 rounded-full blur-sm animate-ping" />
              </div>
              <span className="text-sm font-medium text-purple-300">Available for amazing projects</span>
              <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
            </div>

            {/* Main Title with Enhanced Gradients */}
            <h1 className="text-6xl lg:text-8xl font-black leading-tight">
              <div className="relative inline-block">
                <span className="bg-gradient-to-r from-white via-purple-200 to-pink-200 bg-clip-text text-transparent animate-gradient">
                  Hi, I'm
                </span>
                <div className="absolute -inset-1 bg-gradient-to-r from-purple-600 to-pink-600 rounded-lg blur opacity-20 animate-pulse" />
              </div>
              <br />
              <div className="relative inline-block group">
                <span className="bg-gradient-to-r from-purple-400 via-pink-400 to-purple-600 bg-clip-text text-transparent animate-gradient hover:from-purple-300 hover:via-pink-300 hover:to-purple-500 transition-all duration-500">
                  {personalInfo.name}
                </span>
                <div className="absolute -inset-2 bg-gradient-to-r from-purple-600 to-pink-600 rounded-lg blur-lg opacity-0 group-hover:opacity-30 transition-opacity duration-500" />
              </div>
            </h1>

            {/* Typewriter with Glow Effect */}
            <div className="text-2xl lg:text-3xl text-gray-300 relative">
              <div className="absolute -inset-1 bg-gradient-to-r from-purple-600 to-pink-600 rounded-lg blur opacity-20" />
              <TypewriterText
                strings={personalInfo.typewriterStrings}
                className="text-purple-400 font-bold relative z-10 drop-shadow-lg"
              />
            </div>
          </div>

          {/* Bio with Subtle Animation */}
          <p className="text-xl text-gray-300 max-w-2xl leading-relaxed relative group">
            <span className="relative z-10">{personalInfo.bio}</span>
            <div className="absolute -inset-2 bg-gradient-to-r from-purple-600/10 to-pink-600/10 rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-500" />
          </p>

          {/* Enhanced Action Buttons */}
          <div className="flex flex-col sm:flex-row gap-4">
            <button
              onClick={scrollToContact} 
              className="group relative px-8 py-4 bg-gradient-to-r from-purple-500 to-pink-500 rounded-2xl font-bold text-lg overflow-hidden transition-all duration-500 hover:scale-110 hover:shadow-2xl hover:shadow-purple-500/50 hover:rotate-1"
              onMouseEnter={() => setIsHovered(true)}
              onMouseLeave={() => setIsHovered(false)}
            >
              <span className="relative z-10 flex items-center space-x-2">
                <span>Let's Create Magic</span>
                <ArrowRight className="w-5 h-5 group-hover:translate-x-2 group-hover:scale-110 transition-all duration-300" />
              </span>
              <div className="absolute inset-0 bg-gradient-to-r from-pink-500 to-purple-500 opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
              <div className="absolute inset-0 bg-gradient-to-r from-purple-400 to-pink-400 opacity-0 group-hover:opacity-100 animate-pulse" />
            </button>

            <button 
              onClick={scrollToProjects}
              className="group px-8 py-4 border-2 border-purple-500/70 rounded-2xl font-bold text-lg hover:bg-gradient-to-r hover:from-purple-500/20 hover:to-pink-500/20 transition-all duration-500 hover:border-purple-400 hover:scale-105 hover:shadow-lg hover:shadow-purple-500/25 relative overflow-hidden"
            >
              <span className="relative z-10">Explore My Universe</span>
              <div className="absolute inset-0 bg-gradient-to-r from-purple-600/20 to-pink-600/20 opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
            </button>

            <button 
              onClick={openResume}
              className="group flex items-center space-x-2 px-8 py-4 bg-gradient-to-r from-emerald-500 to-cyan-500 rounded-2xl font-bold text-lg hover:scale-110 transition-all duration-500 hover:shadow-2xl hover:shadow-emerald-500/50 hover:-rotate-1 relative overflow-hidden"
            >
              <ExternalLink className="w-5 h-5 group-hover:rotate-12 transition-transform duration-300" />
              <span>Resume Portal</span>
              <div className="absolute inset-0 bg-gradient-to-r from-cyan-500 to-emerald-500 opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
            </button>
          </div>

          {/* Enhanced Social Links */}
          <div className="flex space-x-4">
            {socialLinks.map((link, index) => {
              const Icon = getIcon(link.icon);
              return (
                <a
                  key={index}
                  href={link.url}
                  className="group relative p-4 bg-white/10 backdrop-blur-sm rounded-2xl hover:bg-white/20 transition-all duration-500 hover:scale-125 hover:shadow-lg hover:shadow-purple-500/25 hover:rotate-12"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <Icon className="w-6 h-6 group-hover:scale-110 transition-transform duration-300" />
                  <div className="absolute inset-0 bg-gradient-to-r from-purple-600/20 to-pink-600/20 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                </a>
              );
            })}
          </div>
        </div>

        {/* Revolutionary Profile Section */}
        <div className="relative">
          {/* Outer Glow */}
          <div className="absolute inset-0 bg-gradient-to-r from-purple-500/30 to-pink-500/30 rounded-full blur-3xl animate-pulse" />

          {/* Main Profile Container */}
          <div className="relative w-96 h-96 mx-auto">
            {/* Rotating Rings */}
            <div className="absolute inset-0 border-4 border-gradient-to-r from-purple-400 to-pink-400 rounded-full animate-spin-slow opacity-60" />
            <div className="absolute inset-4 border-2 border-gradient-to-r from-pink-400 to-purple-400 rounded-full animate-spin-reverse opacity-40" />
            <div className="absolute inset-8 border border-gradient-to-r from-purple-400 to-pink-400 rounded-full animate-spin-slow opacity-20" />

            {/* Profile Image Container */}
            <div className="absolute inset-12 bg-gradient-to-br from-purple-400 via-pink-400 to-purple-600 rounded-full p-1 animate-pulse">
              <div className="w-full h-full bg-gradient-to-br from-slate-900 to-purple-900 rounded-full flex items-center justify-center overflow-hidden relative">
                <div className="absolute inset-0 bg-gradient-to-br from-purple-400 via-purple-600 to-transparent opacity-70 z-10 animate-pulse" />
                <img 
                  src="/images/profile/avatar.jpg" 
                  alt="Darshan Dubey" 
                  className="w-full h-full object-cover transform transition-all duration-700 ease-out hover:scale-110 hover:rotate-6 hover:brightness-125 hover:contrast-110 hover:saturate-150 z-20 relative"
                />

                {/* Image Overlay Effects */}
                <div className="absolute inset-0 bg-gradient-to-t from-purple-900/50 to-transparent z-15" />
                <div className="absolute inset-0 bg-gradient-to-br from-transparent via-purple-500/20 to-pink-500/20 z-15" />
              </div>
            </div>

            {/* Floating Action Icons */}
            {floatingIcons.map((Icon, index) => (
              <div
                key={index}
                className="absolute p-3 bg-gradient-to-r from-purple-500/80 to-pink-500/80 backdrop-blur-sm rounded-2xl shadow-lg hover:shadow-purple-500/50 transition-all duration-500 hover:scale-125 hover:rotate-12"
                style={{
                  top: `${15 + Math.sin((index * Math.PI) / 4) * 35}%`,
                  left: `${15 + Math.cos((index * Math.PI) / 4) * 35}%`,
                  animationDelay: `${index * 0.2}s`,
                }}
              >
                <Icon className="w-5 h-5 text-white animate-pulse" />
              </div>
            ))}

            {/* Orbiting Elements */}
            <div className="absolute inset-0 animate-spin-slow">
              <div className="absolute -top-4 left-1/2 transform -translate-x-1/2 p-4 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-2xl shadow-lg animate-bounce">
                <Code className="w-6 h-6 text-white" />
              </div>
            </div>

            <div className="absolute inset-0 animate-spin-reverse">
              <div className="absolute top-1/2 -right-4 transform -translate-y-1/2 p-4 bg-gradient-to-r from-green-500 to-emerald-500 rounded-2xl shadow-lg animate-pulse">
                <Zap className="w-6 h-6 text-white" />
              </div>
            </div>

            <div className="absolute inset-0 animate-spin-slow">
              <div className="absolute -bottom-4 left-1/2 transform -translate-x-1/2 p-4 bg-gradient-to-r from-orange-500 to-red-500 rounded-2xl shadow-lg animate-bounce delay-500">
                <Rocket className="w-6 h-6 text-white" />
              </div>
            </div>
          </div>

          {/* Ambient Light Effect */}
          <div className="absolute inset-0 bg-gradient-to-r from-purple-500/10 to-pink-500/10 rounded-full blur-2xl animate-pulse" />
        </div>
      </div>

      {/* Custom Styles */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(180deg); }
        }

        @keyframes spin-slow {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes spin-reverse {
          from { transform: rotate(360deg); }
          to { transform: rotate(0deg); }
        }

        @keyframes gradient {
          0%, 100% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
        }

        .animate-float {
          animation: float 4s ease-in-out infinite;
        }

        .animate-spin-slow {
          animation: spin-slow 8s linear infinite;
        }

        .animate-spin-reverse {
          animation: spin-reverse 10s linear infinite;
        }

        .animate-gradient {
          background-size: 200% 200%;
          animation: gradient 3s ease infinite;
        }
      `}</style>
    </section>
  );
};

export default Hero;
